/*
 * Java program to print uppercase and lowercase alphabets
 */

package svikexercise;

public class UpperLower {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(char c='A'; c<='Z';c++) {
			System.out.print(c+" ");
		}
		System.out.println();
		for(char c='a';c<='z';c++) {
			System.out.print(c+" ");
		}

	}

}
